function getRandomColor() {
  let r = Math.floor(Math.random() * 256);
  let g = Math.floor(Math.random() * 256);
  let b = Math.floor(Math.random() * 256) + 100;


  return `rgba(${r},${g},${b})`
}

function setRandomColor(elem) {
  elem.style.color = getRandomColor();
}